<template>
    <span 
        @mouseover="float = true"
        @mouseleave="float = false"
        :class="componentName"
    >
        <transition name="fade" appear>
            <span 
                v-if="float"
                class="h-text"
            >   
                {{ hoverText }}
            </span>
        </transition>

        <p class="text">
            {{ text }}
        </p>

        <slot name="element"></slot>
    </span>
</template>




<script>
    export default {
        name: 'HoverText',
        props: {
            componentName: {
                default: 'hover-text',
                type: String
            },
            text: {
                default: '',
                type: String
            },
            hoverText: {
                default: '',
                type: String
            }
        },
        data() {
            return {
                float: false
            }
        }
    }
</script>
