<template>
	<transition name="modal-side">
		<div 
			v-if="$store.state.isVisible.modalSide" 
			class="modal-side"
		>
			<section class="modal-side-content">
				<span 
					class="no-style close" 
					@click="toggleVisible('modalSide')"
					aria-label="Close this modal"
				>
					<span hidden>Close side modal</span>

					<icon-close />
				</span>

				<slot>
				</slot>			
			</section>
		</div>			
	</transition>
</template>




<script>
	export default {
	    name: "ModalSide",	
		methods: {
			toggleVisible(toToggle) {
				this.$store.commit('toggleVisible', toToggle);
			}, 
		}
	}	
</script>