<template>
	<transition name="mobile-center">
		<div 
			v-if="$store.state.isVisible.modalCenter"
			:class="componentName"
		>
			<div class="for-overflow">
				<div class="content">
					<span 
						class="no-style close" 
						@click="toggleVisible()"
						aria-label="Close this modal"
					>
						<span hidden>Close site search</span>

						<icon-close />
					</span>

					<slot>
					</slot>
				</div>
			</div>
		</div>
	</transition>
</template>




<script>
	export default {
		name: "ModalCenter",	
		props: {
			componentName: {
				default: 'modal-center',
				type: String
			},
			toToggleVisible: {
				default: 'modalCenter',
				type: String
			}
		},
		methods: {
			toggleVisible() {
				this.$store.commit('toggleVisible', this.toToggleVisible);
			}, 
		}
	}
</script>