<template>
	<div :class="[componentName, { 'active' : isActive }, status ]" @click="isActive = false">
		<icon-tooltip />

		<p>{{ message }}</p>

		<icon-close />
	</div>
</template>




<script>
	export default {
		name: "Toast1",	
		props: {
			componentName: {
				default: "toast-1",
				type: String
			},
			message: {
				default: null,
				type: String
			},
			status: {
				default: "success",
				type: String
			},
			displayTime: {
				default: 5000,
				type: Number
			} 
		},
		data () {
			return {
				isActive: false
			}
		},
		watch: {
			isActive() {
				setTimeout(function () {
				    this.isActive = false
				}.bind(this), this.displayTime)
			}
		}
	}
</script>