<template>
    <nav :class="componentName" role="navigation" aria-label="Pagination Navigation">
        <ul>
            <li class="first" @click="requestPage('first')" :disabled="metaData.current_page === 1"><span hidden>Go to first page</span></li>
            <li class="prev" @click="requestPage('prev')" :disabled="metaData.current_page === 1"><span hidden>Go back one page</span></li>

            <!-- <li v-for="(page, index) in metaData.total" :class="{ 'active' : metaData.current_page === page }" @click="requestPage(index + 1)">{{ page }}</li> -->

            <li class="next" @click="requestPage('next')" :disabled="metaData.current_page === metaData.last_page"><span hidden>Go forward a page</span></li>
            <li class="last" @click="requestPage('last')" :disabled="metaData.current_page === metaData.last_page"><span hidden>Go to last page</span></li>
        </ul>
    </nav>
</template>




<script>
    export default {
        name: "Pagination1",
        props: {
            componentName: {
                default: 'pagination-1',
                type: String
            },
            perPage: {
                type: Number,
                default: 20
            },
            metaData: {
                default: {},
                type: Object
            },
        },
        computed: {
            
        },
        methods: {
            requestPage(requested) {
                this.$emit('requested', requested);

            },
        },
    }
</script>
