<template>
    <section class="cf" :class="id">
        <div class="col-3">
            <h1>{{ heading }}</h1>
        </div>

        <div class="for-animation col-9">
            <ul class="col-3 navigation">
                <li v-for="(data, key, index) in tabcontent" :class="{ 'active' : showingTab == index }" @click="showTab(index)">
                    <span>{{ key }}</span>
                </li>
            </ul>

            <div class="col-9">
                <transition name="slide" out-in>
                    <ul v-if="index == showingTab" v-for="(all, key,index) in tabcontent" class="content" :key="key">
                        <li v-for="(data, key) in all">{{ data }}</li>
                    </ul>
                </transition>
            </div>
        </div>
    </section>
</template>




<script>
    export default {
        name: 'TabSide1',
        props: [
            "id",
            "heading",
            "tabcontent"
        ],
        data () {
            return {
                showingTab: 0
            }
        },
        methods: {
            showTab: function (val) {
                this.showingTab = val;
            }
        },
        mounted() {
        }
    }
</script>